import ProviderInterface from "../Core/ProviderInterface";
import User from "../Core/User";
/**
 * @see [Taobao - 用户授权介绍](https://open.taobao.com/doc.htm?docId=102635&docType=1&source=search)
 */
export default class Taobao extends ProviderInterface {
    static NAME: string;
    protected _baseUrl: string;
    protected _gatewayUrl: string;
    protected _scopes: string[];
    protected _view: string;
    protected withView(view: string): this;
    protected getAuthUrl(): string;
    protected getCodeFields(): object;
    protected getTokenUrl(): string;
    protected getTokenFields(code: string): object;
    tokenFromCode(code: string): Promise<object>;
    protected generateSign(params: object): string;
    protected getPublicFields(token: string, apiFields?: object): {
        app_key: string;
        sign_method: string;
        session: string;
        timestamp: any;
        v: string;
        format: string;
    };
    protected getUserInfoUrl(url: string, token: string): string;
    protected getUserByToken(token: string): Promise<object>;
    protected mapUserToObject(user: object): User;
}
